    let z = -rp * Math.sin(h);

    let y = rph;

    let v3 = new THREE.Vector3(x, y, z).applyMatrix4(constants.c_OpenGLToDS3Mx4);

    let realPoint = constants.c_StationInfo.point.clone().add(v3);

    return realPoint;
}

/**
 * 获得墙面点击的真实坐标
 * @param {THREE.Object} obj 
 */
export function getWallRealPoint(obj) {

    let drc = obj.point.clone().applyMatrix4(obj.object.matrix).applyMatrix4(constants.c_OpenGLToDS3Mx4);

    let dx = drc.x / constants.c_WallDisplaySize * 2;

    let dy = drc.y / constants.c_WallDisplaySize * 2;

    let dz = drc.z / constants.c_WallDisplaySize * 2;

    let realPoint = constants.c_StationInfo.point.clone().add(new THREE.Vector3(dx, dy, dz));

    return realPoint;
}

/**
 * 获取是否跳转还是放大
 * @param {THREE.Object} obj 射线对象
 * @param {int} type 1-地面片检测 2-墙面片检测
 */
export function getJudgeOrZoom(obj, type) {

    let distances;

    let dis = 0;

    if (type == 1) {

        distances = getPanoRealPoint(obj, 2.5);

        dis = -1;

    } else {
        distances = getWallRealPoint(obj);
    }
    let dis0 = Math.sqrt(Math.pow((distances.x - constants.c_StationInfo.nx), 2) + Math.pow((distances.y - constants.c_StationInfo.ny), 2)) + dis;

    let panoid = "";

    constants.c_AdjacentPanoInfoArr.forEach((arrowData, idx) => {

        let dis1 = Math.sqrt(Math.pow((distances.x - arrowData.nX), 2) + Math.pow((distances.y - arrowData.nY), 2));

        if (dis1 <= dis0) {

            dis0 = dis1;

            panoid = arrowData.dstPanoID;

        }
    });

    return panoid;
}

/**
 * 获取两个站点坐标点的夹角
 * @param v1 当前站点
 * @param v2 下一站点
 * */
export function getArrowsAngle(v1, v2) {

    let p2 = new THREE.Vector3(v2.x - v1.x, v2.y - v1.y, v2.z - v1.z).applyMatrix4(constants.c_DS3ToOpenGLMx4);

    let v4 = new THREE.Vector3(-1, 0, 0);

    let angle = p2.angleTo(v4);

    angle = THREE.Math.radToDeg(angle); //转换为角度

    let tan = p2.clone().cross(v4).dot(new THREE.Vector3(0, 1, 0));

    if (tan > 0) {

        angle = 360 - angle;

    }

    return angle;
}

/**
 * 清空对象缓存
 * @param node 对象
 * @param ifParent 是否用父类删除自己
 * */
export function disposeNode(node, ifParent = true) {

    if (!node) return;

    if (node instanceof THREE.Mesh || node instanceof THREE.Sprite || node instanceof THREE.Line || node instanceof THREE.BoxHelper || node instanceof THREE.Group) {

        if (node.geometry) {

            node.geometry.dispose();

        }

        if (node.material) {

            if (node.material instanceof THREE.MeshFaceMaterial || node.material instanceof THREE.MultiMaterial) {

                $.each(node.material.materials, function (idx, mtrl) {

                    if (mtrl.map) mtrl.map.dispose();

                    if (mtrl.lightMap) mtrl.lightMap.dispose();

                    if (mtrl.bumpMap) mtrl.bumpMap.dispose();

                    if (mtrl.normalMap) mtrl.normalMap.dispose();

                    if (mtrl.specularMap) mtrl.specularMap.dispose();

                    if (mtrl.envMap) mtrl.envMap.dispose();

                    mtrl.dispose(); // disposes any programs associated with the material
                });

            } else {

                if (node.material.map) node.material.map.dispose();

                if (node.material.lightMap) node.material.lightMap.dispose();

                if (node.material.bumpMap) node.material.bumpMap.dispose();

                if (node.material.normalMap) node.material.normalMap.dispose();

                if (node.material.specularMap) node.material.specularMap.dispose();

                if (node.material.envMap) node.material.envMap.dispose();

                node.material.dispose(); // disposes any programs associated with the material
            }
        }
        if (ifParent) {

            if (node.parent)node.parent.remove(node);

            node = null;
        }
    }
    // 清除缓存
    if (THREE.Cache && THREE.Cache.enabled) {

        THREE.Cache.clear();

    }
}

/**
 * 全屏
 * */
export function getFullScreen() {
    if (!document.fullscreenElement // alternative standard method
        &&
        !document.mozFullScreenElement &&
        !document.webkitFullscreenElement &&
        !document.msFullscreenElement) { // current working methods
        if (document.documentElement.requestFullscreen) {
            document.documentElement.requestFullscreen();
        } else if (document.documentElement.msRequestFullscreen) {
            document.documentElement.msRequestFullscreen();
        } else if (document.documentElement.mozRequestFullScreen) {
            document.documentElement.mozRequestFullScreen();
        } else if (document.documentElement.msRequestFullscreen) { //IE11
            document.documentElement.msRequestFullscreen();
        } else if (document.documentElement.webkitRequestFullscreen) {
            document.documentElement.webkitRequestFullscreen(Element.ALLOW_KEYBOARD_INPUT);
        }
    } else {
        if (document.exitFullscreen) {
            document.exitFullscreen();
        } else if (document.msExitFullscreen) {
            document.msExitFullscreen();
        } else if (document.mozCancelFullScreen) {
            document.mozCancelFullScreen();
        } else if (document.msExitFullscreen) {
            document.msExitFullscreen();
        } else if (document.webkitExitFullscreen) {
            document.webkitExitFullscreen();
        }
    }
}

/**
 * DIV文本显示
 * @param {*} labelPos 三维坐标
 * @param {*} fontSize 字体大小
 * @param {*} text 文本
 */
export function TextDiv(labelPos, fontSize, text) {

    labelPos.copy(getWorldToScene(labelPos));

    this.textDiv = document.createElement("div");

    let fs = fontSize || 14;

    this.textDiv.style.fontSize = fs + "px";

    this.textDiv.style.color = "#fff";

    this.textDiv.style.position = "absolute";

    this.textDiv.id = "" + text;

    this.textDiv.style.pointerEvents = "none";

    if (!text) {

        this.textDiv.style.left = "-100px";

        this.textDiv.style.top = "-100px";

    } else {

        this.textDiv.style.left = labelPos.x + "px";

        this.textDiv.style.top = labelPos.y + "px";

    }
    this.textDiv.innerHTML = text;

    document.body.appendChild(this.textDiv);

    return this.textDiv;
}

/**
 * 清楚新增文本div层
 * @param {*} textdiv 
 */
export function delectTextDiv(textdiv) {
    if (textdiv) {

        let child = document.getElementById(textdiv.id);

        child && child.parentNode.removeChild(child);

        textdiv.parent && textdiv.parent.removeChild(textdiv);

        textdiv = null;

    }
}

/**
 * 获取字体
 * @param {string} fontUrl 字体地址 '../../../commons/font/optimer_regular.typeface.json'
 * @param {Function} callFun 回调函数
 */
export function getFont(fontUrl) {

    return new Promise((resolve, reject) => {

        let loader = new THREE.FontLoader();

        loader.load(fontUrl, (response) => {

            resolve(response);
            
        });
    });
}

/**
 * 设置相机视角
 * @param {Number} yaw 偏航角
 * @param {Number} pitch 俯仰角
 * @param {boolean} move 是否动画旋转
 */
export function setCameraAngle(yaw, pitch, move) {
    if (move) {
        let r1, r2;
        r1 = constants.sw_cameraManage.yaw_Camera - yaw;
        if (r1 > 0) {
            r2 = 360 - constants.sw_cameraManage.yaw_Camera + yaw;
        } else {
            r2 = 360 + constants.sw_cameraManage.yaw_Camera - yaw;
        }

        let from = {
            x: constants.sw_cameraManage.yaw_Camera,
            y: constants.sw_cameraManage.picth_Camera,
            z: 0
        };
        let to = {
            x: (r1 > 0 ? (Math.abs(r1) > Math.abs(r2) ? (yaw + 360) : yaw) : (Math.abs(r1) > Math.abs(r2) ? (yaw - 360) : yaw)),
            y: pitch,
            z: 0
        };
        new TWEEN.Tween(from)
            .to(to, 1000)
            .easing(TWEEN.Easing.Exponential.Out)
            .onUpdate(function () {
                constants.sw_cameraManage.setHousesViewAngle(from.x, from.y, true);
            })
            .onComplete(function () {
                constants.sw_cameraManage.setHousesViewAngle(to.x, to.y, true);
            })
            .start();
    } else {
        constants.sw_cameraManage.setHousesViewAngle(yaw, pitch, true);
    }
}

/**
 * 获取文本框宽高
 * @param {String} fontSize 字号
 * @param {String} fontFamily 字体
 * @param {String} text 文本
 */
export function textSize(fontSize, fontFamily, text) {

    let span = document.createElement("span");

    span.id = "span";

    let result = {};

    result.width = span.offsetWidth;

    result.height = span.offsetHeight;

    span.style.visibility = "hidden";

    span.style.fontSize = fontSize;

    span.style.fontFamily = fontFamily;

    span.style.display = "inline-block";

    document.body.appendChild(span);

    if (typeof span.textContent != "undefined") {

        span.textContent = text;

    } else {

        span.innerText = text;

    }

    result.width = parseFloat(window.getComputedStyle(span).width) - result.width;

    result.height = parseFloat(window.getComputedStyle(span).height) - result.height;

    delectTextDiv(span);

    return result;
}
